import React,{Component} from 'react'
import axios from 'axios'

const api = axios.create({
  baseURL:'http://localhost:8000/list'
})

export class AddEntryPage extends Component{
  state = {
    name:'',
    itemName:'',
    itemPrice:0.0,
    itemLink:''
  }
  handleName = event => {
    this.setState({name:event.target.value})
  }
  handleItemName = event => {
    this.setState({itemName:event.target.value})
  }
  handleItemPrice = event => {
    this.setState({itemPrice:event.target.value})
  }
  handleItemLink = event => {
    this.setState({itemLink:event.target.value})
  }
  handleSubmit = event =>{
    event.preventDefault();

    const input = {
      name:this.state.name,
      itemName:this.state.itemName,
      itemPrice:this.state.itemPrice,
      itemLink:this.state.itemLink
    };
    api.post('./addEntry',{input}).then(res=>{
      console.log(res);
      console.log(res.data);
    })
  }
  render(){
    return (
    <div class="vh-100 bg-secondary text-center">
      <form onSubmit={this.handleSubmit}>
        <label for="NameInput" class="form-label fw-bold text-capitalize fs-4">Name</label>
        <div class="d-flex justify-content-center">
          <input type="name" class="form-control w-25" id="NameInput" placeholder="Name" onChange={this.handleName}></input>
        </div>

        <label for="ItemNameInput" class="form-label fw-bold text-capitalize fs-4">Item Name</label>
        <div class="d-flex justify-content-center">
          <input type="name" class="form-control w-25" id="ItemNameInput" placeholder="Item Name" onChange={this.handleItemName}></input>
        </div>

        <label for="ItemPriceInput" class="form-label fw-bold text-capitalize fs-4">Item Price</label>
        <div class="d-flex justify-content-center">
          <input type="name" class="form-control w-25" id="ItemPriceInput" placeholder="Item Price" onChange={this.handleItemPrice}></input>
        </div>

        <label for="ItemLinkInput" class="form-label fw-bold text-capitalize fs-4">Item Link</label>
        <div class="d-flex justify-content-center">
          <input type="name" class="form-control w-25" id="ItemLinkInput" placeholder="Item Link" onChange={this.handleItemLink}></input>
        </div>
        <div class="d-flex justify-content-center">
        <button type="submit" class="btn btn-primary mt-2" onClick={this.handleSubmit}>Add Item</button>
        </div>
      </form>  
    </div>
  )
  }
}
export default AddEntryPage
